mapper.insert:
INSERT INTO T_USER( USERID, USERPWD, USERNAME, BIRTH, PHONE, EMAIL
,ORIGINALIMAGE, SAVEDIMAGE, INTRODUCE, BELONG
)VALUES( #{userId}, #{userPwd}, #{userName}, #{birth}, #{phone}, #{email}
,#{originalImage}, #{savedImage}, #{introduce}, #{belong}
)

mapper.update:
UPDATE T_USER
SET

	userPwd = #{userPwd}



	BIRTH = #{birth},
	PHONE = #{phone},
	ORIGINALIMAGE = #{originalImage},
	SAVEDIMAGE = #{savedImage},
	INTRODUCE = #{introduce},
	BELONG = #{belong}


WHERE USERID = #{userId}
